mapper.update:
UPDATE dish
name = #{dish.name}, category_id = #{dish.categoryId}, price=#{dish.price}, image=#{dish.image}, description=#{dish.description}, status=#{dish.status}, update_time=#{dish.updateTime}, update_user=#{dish.updateUser},
WHERE id=#{dish.id}

mapper.select:
SELECT
t1.*,
t2.name AS categoryName
FROM dish t1
LEFT JOIN category t2 ON t1.category_id = t2.id


        AND t1.name LIKE CONCAT('%',#{queryDTO.name},'%')


        AND t1.category_id=#{queryDTO.categoryId}


        AND t1.status=#{queryDTO.status}


ORDER BY t1.update_time DESC
LIMIT #{queryDTO.page},#{queryDTO.pageSize}
