mapper.update:
UPDATE setmeal
category_id = #{categoryId}, name = #{name}, price = #{price}, status = #{status}, description = #{description}, image = #{image}, update_time = #{updateTime}, update_user=#{updateUser},
WHERE id = #{id}

mapper.select:
SELECT t1.*,t2.name categoryName FROM setmeal t1 LEFT JOIN category t2
ON t1.category_id = t2.id


        AND t1.name LIKE CONCAT('%',#{name},'%')


        AND t1.category_id=#{categoryId}


        AND t1.status=#{status}


ORDER BY t1.create_time DESC
LIMIT #{page},#{pageSize}
